package com.sapient.training.departmentCatalogue.model.catalogue;

public class DepartmentCount {

	int id;
	int count;
	
	

	public DepartmentCount() {
	
		// TODO Auto-generated constructor stub
	}

	public DepartmentCount(int id, int count) {

		this.id = id;
		this.count = count;
	}

	public int getId() {
		return id;
	}

	public void setId(int id) {
		this.id = id;
	}

	public int getName() {
		return count;
	}

	public void setName(int count) {
		this.count = count;
	}

}
